--Verification  
SELECT sys_context('USERENV','SERVICE_NAME') "DATABASE NAME", SYSDATE "AS OF DATE STATE" FROM DUAL;
SELECT 'EOAWCRTA' "TABLE NAME",REC.EOAWCRTA_ID,REC.DESCR,REC.EOAWCRTA_SOURCE,REC.QRYNAME,REC.SQLID,REC.PACKAGEROOT,REC.APPCLASS_PATH,REC.APP_CLASS,REC.EOAWCRITERIA_FLG FROM PS_EOAWCRTA REC WHERE EOAWCRTA_ID LIKE '%THA012019-10-01%' ORDER BY 1,2;
SELECT 'EOAWCRTA_REC' "TABLE NAME",REC.EOAWCRTA_ID,REC.SEQ_NBR,REC.RECNAME,REC.FIELDNAME,REC.AMT_RECNAME,REC.AMT_FLDNAME,REC.EOAWCRITERIA_FLG,REC.EOAWAMOUNT_SW,REC.EOAWCRTA_OP_NUM,REC.AMOUNT_1,REC.AMOUNT_2,REC.CURRENCY_CD,REC.CUR_RT_TYPE,REC.RECNAME_CUR,REC.FIELDNAME_CUR,REC.DESCR254_MIXED FROM PS_EOAWCRTA_REC REC WHERE EOAWCRTA_ID LIKE '%THA012019-10-01%' ORDER BY 1,2,3,4,5,6,7;
SELECT 'EOAWCRTA_VAL' "TABLE NAME",REC.EOAWCRTA_ID,REC.SEQ_NBR,REC.RECNAME,REC.FIELDNAME,REC.EOAWCRTA_VAL1,REC.EOAWCRTA_OPERATOR,REC.EOAWCRTA_VAL2,REC.EOAWCRTA_VAL3 FROM PS_EOAWCRTA_VAL REC WHERE EOAWCRTA_ID LIKE '%THA012019-10-01%' ORDER BY 1,2,3,4,5,6;
SELECT 'EOAW_PATH' "TABLE NAME",REC.EOAWPRCS_ID,REC.EOAWDEFN_ID,REC.EFFDT,REC.EOAWSTAGE_NBR,REC.EOAWPATH_ID,REC.EOAWSTEP_SOURCE,REC.EOAWNUMBER_HOURS,REC.EOAWNUMBER_DAYS,REC.EOAWESCALATN_OPTN,REC.DESCR,REC.EOAWCRTA_ID,REC.EOAWSKIP_PRIOR,REC.EOAWAUTH_SW,REC.EOAWFIRST_STEP_SW,REC.EOAWREQUIREFIRST,REC.EOAWSKIP_UNAUTHED,REC.DESCRLONG,REC.EOAWNOTIFY_ADMIN FROM PS_EOAW_PATH REC WHERE EOAWCRTA_ID LIKE '%THA012019-10-01%' AND EFFDT = TO_DATE('1/10/2019','DD/MM/YYYY') AND EOAWDEFN_ID = 'THA01' AND EOAWPRCS_ID = 'PaymentRequestApproval' ORDER BY 1,2,3,4,5,6;
SELECT 'EOAW_PRCS' "TABLE NAME",REC.EOAWPRCS_ID,REC.EOAWDEFN_ID,REC.EFFDT,REC.EFF_STATUS,REC.DESCR,REC.EOAWADMIN_ROLENAME,REC.EOAWACT_LINE_LEVEL,REC.EOAWAUTO_APPROVE,REC.EOAWFIELD_LIST_ID,REC.EOAWCRTA_ID,REC.EOAWDEFN_CRTA_ID,REC.EOAW_REQSTR_SW,REC.EOAW_INCL_REQSTR,REC.EOAWDEFN_DEFAULT,REC.EOAWDEFN_PRIORITY,REC.EOAW_CALENDAR FROM PS_EOAW_PRCS REC WHERE EOAWCRTA_ID LIKE '%THA012019-10-01%' AND EFFDT = TO_DATE('1/10/2019','DD/MM/YYYY') AND EOAWDEFN_ID = 'THA01' AND EOAWPRCS_ID = 'PaymentRequestApproval' ORDER BY 1,2,3,4;
SELECT 'EOAW_STAGE' "TABLE NAME",REC.EOAWPRCS_ID,REC.EOAWDEFN_ID,REC.EFFDT,REC.EOAWSTAGE_NBR,REC.DESCR,REC.SEQ_NBR,REC.EOAWLEVEL FROM PS_EOAW_STAGE REC WHERE EOAWDEFN_ID = 'THA01' AND EOAWPRCS_ID = 'PaymentRequestApproval' AND SEQ_NBR IN (170,169,179)  AND EFFDT = TO_DATE('1/10/2019','DD/MM/YYYY')  ORDER BY 1,2,3,4,5;
SELECT 'EOAW_STEP' "TABLE NAME",REC.EOAWPRCS_ID,REC.EOAWDEFN_ID,REC.EFFDT,REC.EOAWSTAGE_NBR,REC.EOAWPATH_ID,REC.EOAWSTEP_NBR,REC.SEQ_NBR,REC.EOAWAPPROVER_LIST,REC.EOAWAPPROVER_SW,REC.EOAWMIN_APPROVERS,REC.EOAWROLENAME,REC.EOAWSELF_APPROVAL,REC.EOAWSELF_CRTA_ID,REC.EOAWREVIEWER_LIST,REC.EOAW_REQSTR_SW,REC.EOAW_FILTER_REQ,REC.DESCR,REC.EOAWCRTA_ID,REC.EOAW_EXTERN_FLAG FROM PS_EOAW_STEP REC WHERE EOAWCRTA_ID LIKE '%THA012019-10-01%' AND EFFDT = TO_DATE('1/10/2019','DD/MM/YYYY') AND EOAWDEFN_ID = 'THA01' AND EOAWPRCS_ID = 'PaymentRequestApproval' ORDER BY 1,2,3,4,5,6,7;
SELECT 'EOAW_TIMEOUT' "TABLE NAME",REC.EOAWPRCS_ID,REC.EOAWDEFN_ID,REC.EFFDT,REC.EOAWSTAGE_NBR,REC.EOAWPATH_ID,REC.SEQ_NBR,REC.EOAWESCALATN_OPTN,REC.EOAWNUMBER_HOURS,REC.EOAWNUMBER_DAYS,REC.EOAWREASSIGNOPRID,REC.EOAWUSER_LIST_ID,REC.TIMEOUTMINUTES,REC.EOAW_TIMEOUTMINUTE,REC.EOAWACTUAL_APPR FROM PS_EOAW_TIMEOUT REC WHERE EFFDT = TO_DATE('1/10/2019','DD/MM/YYYY')  AND EOAWPRCS_ID = 'PaymentRequestApproval' AND EOAWDEFN_ID = 'THA01' ORDER BY 1,2,3,4,5,6,7;